let items = $input.all();

let retItems = [];
for (let i = 0; i < items.length; i++) {
  let topic = items[i].json.topic;
  let topicValue = items[i].json.message;

  let topicProps = topic.split("/");
  let topicPropsLength = topicProps.length;

  retItems.push({
      "companyName": topicProps[topicPropsLength-4],
      "productId": topicProps[topicPropsLength-3],
      "deviceId": topicProps[topicPropsLength-2],
      "propertyName": topicProps[topicPropsLength-1],
      "propertyValue": String(topicValue),
  });
}

return retItems;
